dict.string:
query="{query}";

a=("${(@s/|/)query}")
color=`echo -n $a[1] | tr -d '[[:space:]]'`;
port=`echo -n $a[2] | tr -d '[[:space:]]'`;

if [[ -z $port ]]; then
	echo -n $color | nc -4u -w0 localhost 1738;
else
	echo -n $color | nc -4u -w0 localhost $port;
fi

#########################
# Contants.
#########################
VPREFS="${HOME}/Library/Caches/com.runningwithcrayons.Alfred-2/Workflow Data/"
NVPREFS="${HOME}/Library/Application Support/Alfred 2/Workflow Data/"

###############################################################################
# Read the bundleid from the workflow's info.plist
###############################################################################
getBundleId() {
  /usr/libexec/PlistBuddy  -c "Print :bundleid" "info.plist"
}

###############################################################################
# Get the workflow data dir
###############################################################################
getDataDir() {
  local BUNDLEID=$(getBundleId)
  echo "${NVPREFS}${BUNDLEID}"
}

if [ ! -d "$(getDataDir)" ]; then
   mkdir -p "$(getDataDir)";
   echo -n "1738" > "$(getDataDir)/port.txt";
fi

query="{query}";

port=`cat "$(getDataDir)/port.txt"`;

echo -n "$query|$port";

VPREFS="${HOME}/Library/Caches/com.runningwithcrayons.Alfred-2/Workflow Data/" NVPREFS="${HOME}/Library/Application Support/Alfred 2/Workflow Data/"
RESULTS=()
################################################################################ # Adds a result to the result array # # $1 uid # $2 arg # $3 title # $4 subtitle # $5 icon # $6 valid # $7 autocomplete ############################################################################### addResult() { RESULT="<item uid='$(xmlEncode "$1")' arg='$(xmlEncode "$2")' valid='$6' autocomplete='$7'><title>$(xmlEncode "$3")</title><subtitle>$(xmlEncode "$4")</subtitle><icon>$(xmlEncode "$5")</icon></item>" RESULTS+=("$RESULT") }
addResultConditional() { qu=`echo -n $1 | tr '[:upper:]' '[:lower:]'`; ti=`echo -n $4 | tr '[:upper:]' '[:lower:]'`; if [[ $ti == *$qu* ]]; then addResult "$2" "$3" "$4" "$5" "$6" "$7" "$8"; fi }
############################################################################### # Prints the feedback xml to stdout ############################################################################### getXMLResults() { echo -n "<?xml version='1.0'?><items>"
# if [ "${#string[@]}" = "0" ]; then # echo -n "<item uid='oftask' arg='-' valid='no'><title>No results found</title><subtitle>Please try another search term</subtitle><icon></icon></item> " # fi
for R in ${RESULTS[*]}; do echo -n "$R " done
echo -n "</items> " }
############################################################################### # Escapes XML special characters with their entities ############################################################################### xmlEncode() { echo -n "$1" | sed 's/\n/ /' | sed 's/&/&amp;/' | sed 's/>/&gt;/' | sed 's/</&lt;/' | sed "s/\'/&apos;/" | sed 's/\"/&quot;/' }
############################################################################### # Read the bundleid from the workflow's info.plist ############################################################################### getBundleId() { /usr/libexec/PlistBuddy -c "Print :bundleid" "info.plist" }
############################################################################### # Get the workflow data dir ############################################################################### getDataDir() { local BUNDLEID=$(getBundleId) echo -n "${NVPREFS}${BUNDLEID}" }
############################################################################### # Get the workflow cache dri ############################################################################### getCacheDir() { local BUNDLEID=$(getBundleId) echo -n "${VPREFS}${BUNDLEID}" }
############################################################################### # Save key=value to the workflow properties # # $1 key # $2 value # $3 non-volatile 0/1 # $4 filename (optional, filename will be "settings" if not specified) ############################################################################### setPref() { local BUNDLEID=$(getBundleId) if [ "$3" = "0" ]; then local PREFDIR="${VPREFS}${BUNDLEID}" else local PREFDIR="${NVPREFS}${BUNDLEID}" fi
if [ ! -d "$PREFDIR" ]; then mkdir -p "$PREFDIR" fi
if [ -z "$4" ]; then local PREFFILE="${PREFDIR}/settings" else local PREFFILE="${PREFDIR}/$4" fi
if [ ! -f "$PREFFILE" ]; then touch "$PREFFILE" fi
local KEY_EXISTS=$(grep -c "$1=" "$PREFFILE") if [ "$KEY_EXISTS" = "0" ]; then echo -n "$1=$2" >> "$PREFFILE" else sed -i "" s/"$1=.*"/"$1=$2"/ "$PREFFILE" fi }
############################################################################### # Read a value for a given key from the workflow preferences # # $1 key # $2 non-volatile 0/1 # $3 filename (optional, filename will be "settings" if not specified) ############################################################################### getPref() { local BUNDLEID=$(getBundleId) if [ "$2" = "0" ]; then local PREFDIR="${VPREFS}${BUNDLEID}" else local PREFDIR="${NVPREFS}${BUNDLEID}" fi
if [ ! -d "$PREFDIR" ]; then return fi
if [ -z "$3" ]; then local PREFFILE="${PREFDIR}/settings" else local PREFFILE="${PREFDIR}/$3" fi
if [ ! -f "$PREFFILE" ]; then return fi
local VALUE=$(sed "/^\#/d" "$PREFFILE" | grep "$1" | tail -n 1 | cut -d "=" -f2-) echo -n "$VALUE" }
query="{query}"
addResultConditional "$query" 'abwhite' "white" 'White Circle' 'AnyBar Workflow' 'icon.png' 'yes' '';
addResultConditional "$query" 'abblue' "blue" 'Blue Circle' 'AnyBar Workflow' 'icon.png' 'yes' '';
addResultConditional "$query" 'abred' "red" 'Red Circle' 'AnyBar Workflow' 'icon.png' 'yes' '';
addResultConditional "$query" 'aborange' "orange" 'Orange Circle' 'AnyBar Workflow' 'icon.png' 'yes' '';
addResultConditional "$query" 'abyellow' "yellow" 'Yellow Circle' 'AnyBar Workflow' 'icon.png' 'yes' '';
addResultConditional "$query" 'abgreen' "green" 'Green Circle' 'AnyBar Workflow' 'icon.png' 'yes' '';
addResultConditional "$query" 'abcyan' "cyan" 'Cyan Circle' 'AnyBar Workflow' 'icon.png' 'yes' '';
addResultConditional "$query" 'abpurple' "purple" 'Purple Circle' 'AnyBar Workflow' 'icon.png' 'yes' '';
addResultConditional "$query" 'abblack' "black" 'Black Circle' 'AnyBar Workflow' 'icon.png' 'yes' '';
addResultConditional "$query" 'abquestion' "question" 'Question' 'AnyBar Workflow' 'icon.png' 'yes' '';
addResultConditional "$query" 'abexclamation' "exclamation" 'Exclamation' 'AnyBar Workflow' 'icon.png' 'yes' '';
for filename in ~/.AnyBar/*.png; do name="$(basename "$filename" .png)"; name=`echo -n $name | tr '\_' ' '`; addResultConditional "$query" "ab$name" "$name" "$name" 'AnyBar Workflow' 'icon.png' 'yes' ''; done
res="$(getXMLResults)";
if [[ $res == *"<items></items>"* ]]; then addResult '999' ' ' 'Sorry, no match. Try Again!' 'AnyBar Workflow' 'icon.png' 'no' ''; fi
echo -n "$(getXMLResults)";

######################### # Contants. ######################### VPREFS="${HOME}/Library/Caches/com.runningwithcrayons.Alfred-2/Workflow Data/" NVPREFS="${HOME}/Library/Application Support/Alfred 2/Workflow Data/"
############################################################################### # Read the bundleid from the workflow's info.plist ############################################################################### getBundleId() { /usr/libexec/PlistBuddy -c "Print :bundleid" "info.plist" }
############################################################################### # Get the workflow data dir ############################################################################### getDataDir() { local BUNDLEID=$(getBundleId) echo "${NVPREFS}${BUNDLEID}" }
if [ ! -d "$(getDataDir)" ]; then mkdir -p "$(getDataDir)"; echo -n "1738" > "$(getDataDir)/port.txt"; fi
query="{query}"
if [[ -z $query ]]; then open -n ~/Applications/AnyBar.app echo -n "1738" > "$(getDataDir)/port.txt"; else ANYBAR_PORT=$query open -n ~/Applications/AnyBar.app echo -n "$query" > "$(getDataDir)/port.txt"; fi

query="{query}"

if [[ -d "~/.AnyBar" ]]; then
	mv "$query" ~/.AnyBar;
else
	mkdir ~/.AnyBar;
	mv "$query" ~/.AnyBar;
fi

#########################
# Contants.
#########################
VPREFS="${HOME}/Library/Caches/com.runningwithcrayons.Alfred-2/Workflow Data/"
NVPREFS="${HOME}/Library/Application Support/Alfred 2/Workflow Data/"

###############################################################################
# Read the bundleid from the workflow's info.plist
###############################################################################
getBundleId() {
  /usr/libexec/PlistBuddy  -c "Print :bundleid" "info.plist"
}

###############################################################################
# Get the workflow data dir
###############################################################################
getDataDir() {
  local BUNDLEID=$(getBundleId)
  echo "${NVPREFS}${BUNDLEID}"
}

if [ ! -d "$(getDataDir)" ]; then
   mkdir -p "$(getDataDir)";
   echo -n "1738" > "$(getDataDir)/port.txt";
fi

query="{query}";

echo -n "$query" > "$(getDataDir)/port.txt";

echo "Set to port: {query}.";

######################### # Contants. ######################### VPREFS="${HOME}/Library/Caches/com.runningwithcrayons.Alfred-2/Workflow Data/" NVPREFS="${HOME}/Library/Application Support/Alfred 2/Workflow Data/"
############################################################################### # Read the bundleid from the workflow's info.plist ############################################################################### getBundleId() { /usr/libexec/PlistBuddy -c "Print :bundleid" "info.plist" }
############################################################################### # Get the workflow data dir ############################################################################### getDataDir() { local BUNDLEID=$(getBundleId) echo "${NVPREFS}${BUNDLEID}" }
if [ ! -d "$(getDataDir)" ]; then mkdir -p "$(getDataDir)"; echo -n "1738" > "$(getDataDir)/port.txt"; fi
if [[ ! -d "${HOME}/.AnyBar" ]]; then mkdir -p "${HOME}/.AnyBar" fi
cp -f ./AnyBar/*.png "${HOME}/.AnyBar"
echo -n "Graphics Copied! ";
